import { createSlice } from "@reduxjs/toolkit";

const uiSlice = createSlice({
    name: 'ui',
    initialState: {showUI: false},
    reducers: {
        toggleShow(state) {
            state.showUI = !state.showUI;
        }
    }
});

export const uiActions = uiSlice.actions;
export default uiSlice.reducer;
